import { AuthService } from '../../auth/service/auth.service';
import { Injectable } from '@angular/core';
import { Actions, Effect, ofType } from '@ngrx/effects';
import { AuthActionTypes } from './auth.actions';
import { mergeMap, map, catchError, tap } from 'rxjs/operators';
import { of } from 'rxjs';
import * as authActions from './auth.actions';
import { User } from '../data-models/user.d';
import { Router } from '@angular/router';

@Injectable()
export class AuthEffects {
  @Effect()
  login$ = this.actions$.pipe(
    ofType(AuthActionTypes.Login),
    mergeMap((action: authActions.Login) =>
      this.authService
        .login(action.payload)
        .pipe(
          map((user: User) => new authActions.LoginSuccess(user)),
          catchError((error) => {
            alert("Login Failed...");
            return of(new authActions.LoginFail(error))
          })
        )
    )
  );

  @Effect({ dispatch: false })
  navigateToProfile$ = this.actions$.pipe(
    ofType(AuthActionTypes.LoginSuccess),
    map((action: authActions.LoginSuccess) => action.payload),
    tap(() => this.router.navigate([`/product/main`]))
  );

  constructor(
    private actions$: Actions,
    private authService: AuthService,
    private router: Router
  ) { }
}

import { environment } from '../../../environments/environment';
import { User } from '../data-models/user.d';
import { Authenticate } from '../data-models/authenticate.d';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';
import { tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  private userSubject$ = new BehaviorSubject<User>(null);
  user$ = this.userSubject$.asObservable();

  constructor(private httpClient: HttpClient) {
    const user = localStorage.getItem('user');
    if (user) {
      this.userSubject$.next(JSON.parse(user));
    }
  }

  // login user
  
  login(authenticate: Authenticate): Observable<User> {
    return this.httpClient.post<User>(
      environment.api_url + 'login',
      authenticate
    ).pipe(tap((user: User) => {
        this.userSubject$.next(user);
        localStorage.setItem('user', JSON.stringify(user));
    }));
  }
}

import { Product } from './../../../auth/data-models/product.d';
import { Injectable } from '@angular/core';
import { Actions, Effect, ofType } from '@ngrx/effects';
import { ProductsService } from './../services/products/products.service';
import { ProductsActionTypes } from './../+state/products.actions';
import { mergeMap, map, tap, catchError, filter, switchMap } from 'rxjs/operators';
import * as productActions from './../+state/products.actions';
import { of } from 'rxjs';
import { ROUTER_NAVIGATION, RouterNavigationAction } from '@ngrx/router-store';

@Injectable()
export class ProductsEffects {
  @Effect()
  loadFilteredProducts$ = this.actions$.pipe(
    ofType(ROUTER_NAVIGATION),
    filter((r: RouterNavigationAction) => r.payload.routerState.url.startsWith('/products')),
    map((r: RouterNavigationAction) => r.payload.routerState.root.queryParams.category),
    mergeMap((category: string) =>
      this.productService
        .getProducts(category)
        .pipe(
          map((products: Product[]) => new productActions.LoadProductsSuccess(products)),
          catchError(error => of(new productActions.LoadProductsFail(error)))
        )
    )
  );

  @Effect()
  loadProducts$ = this.actions$.pipe(
    ofType(ProductsActionTypes.LoadProducts),
    mergeMap(() =>
      this.productService
        .getProducts()
        .pipe(
          map(
            (products: Product[]) =>
              new productActions.LoadProductsSuccess(products)
          ),
          catchError(error => of(new productActions.LoadProductsFail(error)))
        )
    )
  );



  @Effect()
  updateProducts$ = this.actions$.pipe(
    ofType(ProductsActionTypes.UpdateProductsSuccess),
    mergeMap((action) =>
      this.productService
        .updateProducts((action as any).payload)
        .pipe(
          map(
            (products: Product[]) =>
              new productActions.LoadProducts()
          ),
          catchError(error => of(new productActions.UpdateProductsFail(error)))
        )
    )
  );

  constructor(
    private actions$: Actions,
    private productService: ProductsService
  ) { }
}

import { Product } from './../../../../auth/data-models';
import { environment } from './../../../../../environments/environment'
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ProductsService {
  private data: BehaviorSubject<any> = new BehaviorSubject<any>(null);
  constructor(private httpClient: HttpClient) { }


  // Get all products from database
  getProducts(category = null): Observable<Product[]> {
    const url =
      category !== null
        ? environment.api_url + `products?category=${category}`
        : environment.api_url + `products`;

    return this.httpClient.get<Product[]>(url);
  }

  //  Update product based on Id in database
  updateProducts(changes: Partial<Product>): Observable<Product[]> {
    console.log(changes);
    const url = environment.api_url + `products/` + changes.id;
    return this.httpClient.put<Product[]>(url, changes);
  }


// Getter and setter
  public setData(data: any): void {
    this.data.next(data);
  }

  public getData(): Observable<any> {
    return this.data.asObservable();
  }
}

